import nisyscfg


def get_rio_cards():
    with nisyscfg.Session() as session:
        filter = session.create_filter()
        filter.is_present = True
        filter.is_ni_product = True
        out = dict()
        for hw in session.find_hardware(filter):
            if "ni-rio" in hw.expert_name[0]:
                out[hw.name] = hw.product_name
        return out


rio_cards = get_rio_cards()


def alias_to_rio_name(alias: str):
    for k, v in rio_cards.items():
        if k == alias:
            return v


if __name__ == "__main__":
    product = alias_to_rio_name("RIO_7821_1")
    print(product)
    product = alias_to_rio_name("RIO_7822_1")
    print(product)
    product = alias_to_rio_name("RIO_7820_1")
    print(product)
